#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 50000

typedef struct {
    int hp, dp;
} Character;

int compare(const void *a, const void *b) {
    Character *x = (Character *)a;
    Character *y = (Character *)b;
    if (x->hp - y->hp > 0) return 1;
    if (x->hp - y->hp < 0) return -1;
    if (x->dp - y->dp > 0) return 1;
    if (x->dp - y->dp < 0) return -1;
    return 0;
}

int main() {
    int n, C;
    scanf("%d %d", &n, &C);
    Character *v = (Character *)malloc(n * sizeof(Character));
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &v[i].hp, &v[i].dp);
    }
    qsort(v, n, sizeof(Character), compare);
    int ans = 1;
    for (int i = 1; i < n; i++) {
        if (abs(v[i].hp - v[0].hp) > C) {
            ans++;
        } else {
            break;
        }
    }
    for (int i = 1; i < n; i++) {
        if (abs(v[i].hp - v[0].hp) > C) {
            if (v[i].hp > v[0].hp) {
                ans++;
                break;
            }
        } else {
            if (v[i].dp > v[0].dp) {
                ans++;
                break;
            }
        }
    }
    printf("%d\n", ans);
    free(v);
    return 0;
}
