#include <iostream>
#include <vector>
#include <algorithm>

struct Character {
    int hp, dp;
};

bool compareCharacters(const Character& a, const Character& b) {
    if (std::abs(a.hp - b.hp) > C) {
        return a.hp > b.hp;
    } else {
        return a.dp > b.dp;
    }
}

int C;

int main() {
    int N;
    std::cin >> N >> C;

    std::vector<Character> characters(N);
    for (int i = 0; i < N; i++) {
        std::cin >> characters[i].hp >> characters[i].dp;
    }

    std::sort(characters.begin(), characters.end(), compareCharacters);

    int count = 1;
    for (int i = 1; i < N; i++) {
        if (std::abs(characters[i].hp - characters[0].hp) > C) {
            if (characters[i].hp > characters[0].hp) {
                count++;
            }
        } else {
            if (characters[i].dp > characters[0].dp) {
                count++;
            }
        }
    }

    std::cout << count << std::endl;

    return 0;
}
