import java.util.*;
import java.io.*;

public class Main {
    static class Character implements Comparable<Character> {
        int hp, dp;

        public Character(int hp, int dp) {
            this.hp = hp;
            this.dp = dp;
        }

        @Override
        public int compareTo(Character other) {
            if (this.hp == other.hp) {
                return Integer.compare(this.dp, other.dp);
            }
            return Integer.compare(this.hp, other.hp);
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int C = Integer.parseInt(st.nextToken());
        Character[] v = new Character[n];
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            v[i] = new Character(Integer.parseInt(st.nextToken()), Integer.parseInt(st.nextToken()));
        }
        Arrays.sort(v);
        int ans = 1;
        for (int i = 1; i < n; i++) {
            if (Math.abs(v[i].hp - v[0].hp) > C) {
                ans++;
            } else if (Math.abs(v[i].hp - v[0].hp) == C && v[i].dp < v[0].dp) {
                ans++;
            }
        }
        System.out.println(ans);
    }
}
