#include <stdio.h>
#include <math.h>

#define PI 3.14159265358979323846

int main() {
    double x, y, theta, omega;
    scanf("%lf %lf %lf %lf", &x, &y, &theta, &omega);

    theta = theta * PI / 180;
    omega = omega * PI / 180;

    double a = x * sin(theta) - y * cos(theta);
    double b = x * cos(theta) + y * sin(theta);

    double c = sqrt(a * a + b * b);
    double d = atan2(b, a);

    double v = 0;
    double t = 0;
    double dt = 1e-6;
    double t_max = 1e6;

    while (t < t_max) {
        double alpha = omega * dt;
        double t1 = (d - theta + alpha) / omega;
        double t2 = (d - theta - alpha) / omega;

        if (t1 > 0 && t1 < t2) {
            t = t1;
            v = c / (t + dt);
            break;
        } else if (t2 > 0 && t2 < t1) {
            t = t2;
            v = c / (t + dt);
            break;
        } else {
            t += dt;
        }
    }

    printf("%.10lf\n", v);
    return 0;
}
