#include <iostream>
#include <cmath>

using namespace std;

const double PI = acos(-1.0);

double calc(double x, double y, double theta, double omega) {
    double r = sqrt(x * x + y * y);
    double phi = atan2(y, x);
    double t = 0;
    double v = 0;
    double dt = 1e-6;
    double dtheta = omega * dt;
    double dx = 0, dy = 0;

    while (t < 1e6) {
        double dphi = (theta - fmod(phi + PI, 2 * PI)) * PI / 180;
        if (dphi > dtheta) {
            dphi = dtheta;
        } else if (dphi < -dtheta) {
            dphi = -dtheta;
        }
        double dtheta2 = dphi / dt;
        double dphi2 = dtheta2 * dt;
        double dx2 = r * cos(phi + dphi2) - r * cos(phi);
        double dy2 = r * sin(phi + dphi2) - r * sin(phi);
        double v2 = sqrt(dx2 * dx2 + dy2 * dy2) / dt;
        if (v2 > v) {
            v = v2;
        }
        theta += dtheta2 * 180 / PI;
        phi += dphi2;
        dx += dx2;
        dy += dy2;
        if (dx * x + dy * y < 0) {
            return v;
        }
        t += dt;
    }
    return v;
}

int main() {
    double x, y, theta, omega;
    cin >> x >> y >> theta >> omega;
    theta = fmod(theta, 360);
    if (theta < 0) {
        theta += 360;
    }
    double v = 0;
    for (int i = 0; i < 360; i++) {
        double v2 = calc(x, y, theta, omega);
        if (v2 > v) {
            v = v2;
        }
        theta = (theta + 1) % 360;
    }
    cout << fixed << setprecision(10) << v << endl;
    return 0;
}
