#include <stdio.h>
#include <string.h>

#define MAX_N 10000
#define MAX_M 100

int main() {
    int n, m;
    char s[MAX_M][MAX_N + 1];

    scanf("%d %d", &n, &m);
    for (int i = 0; i < m; i++) {
        scanf("%s", s[i]);
    }

    int min_error = n * m;
    int min_left = 0;
    int min_right = n + 1;

    for (int i = 0; i <= n; i++) {
        int error = 0;
        for (int j = 0; j < m; j++) {
            int count_e = 0;
            int count_w = 0;
            for (int k = i; k < n; k++) {
                if (s[j][k] == 'E') {
                    count_e++;
                } else {
                    count_w++;
                }
            }
            if (i < n) {
                error += count_e;
            } else {
                error += count_w;
            }
        }
        if (error < min_error) {
            min_error = error;
            min_left = i;
            min_right = i + 1;
        }
    }

    printf("%d %d\n", min_left, min_right);

    return 0;
}
