#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int n, m;
    cin >> n >> m;

    vector<string> d(m);
    for (int i = 0; i < m; i++) {
        cin >> d[i];
    }

    vector<int> pref(n + 1, 0);
    for (int i = 0; i < m; i++) {
        for (int j = 0; j < n; j++) {
            if (d[i][j] == 'E') {
                pref[j + 1]++;
            }
        }
    }

    int minErr = 2 * n;
    int left = 0, right = n + 1;
    for (int i = 0; i <= n; i++) {
        int err = pref[i] + (n - i) - pref[n];
        if (err < minErr) {
            minErr = err;
            left = i;
            right = n + 1;
        } else if (err == minErr) {
            if (i < left) {
                left = i;
                right = n + 1;
            }
        }
    }

    cout << left << " " << right << endl;

    return 0;
}
