#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 3000
#define MAXD 4

int n;
int x[MAXN], y[MAXN];
char d[MAXN][MAXD];
int dp[MAXN][MAXD];

int dfs(int i, int j) {
    if (dp[i][j] != -1) return dp[i][j];
    int max = 0;
    for (int k = 0; k < n; k++) {
        if (k == i) continue;
        int dx = x[k] - x[i];
        int dy = y[k] - y[i];
        int dir = 0;
        if (dx > 0) dir = 0;
        else if (dx < 0) dir = 2;
        else if (dy > 0) dir = 1;
        else dir = 3;
        if (d[k][dir] == d[i][j]) {
            max = max > dfs(k, dir) ? max : dfs(k, dir);
        }
    }
    return dp[i][j] = max + 1;
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d %d %s", &x[i], &y[i], d[i]);
        for (int j = 0; j < MAXD; j++) dp[i][j] = -1;
    }
    int max = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < MAXD; j++) {
            max = max > dfs(i, j) ? max : dfs(i, j);
        }
    }
    printf("%d\n", max);
    return 0;
}
