#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int N = 3000 + 10;
const int INF = 1e9;

struct Point {
    int x, y;
    char d;
};

int n;
Point p[N];
int f[N][4][4];

int main() {
    cin >> n;
    for (int i = 1; i <= n; i++) {
        cin >> p[i].x >> p[i].y >> p[i].d;
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < 4; j++) {
            for (int k = 0; k < 4; k++) {
                f[i][j][k] = -INF;
            }
        }
    }

    f[1][0][0] = 1;

    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < 4; j++) {
            for (int k = 0; k < 4; k++) {
                if (f[i][j][k] != -INF) {
                    int x = p[i].x, y = p[i].y;
                    int dx = 0, dy = 0;
                    if (j == 0) dx = 1;
                    else if (j == 1) dy = 1;
                    else if (j == 2) dx = -1;
                    else if (j == 3) dy = -1;

                    if (p[i].d == '>') dx = 1;
                    else if (p[i].d == 'v') dy = 1;
                    else if (p[i].d == '<') dx = -1;
                    else if (p[i].d == '^') dy = -1;

                    int nx = x + dx, ny = y + dy;
                    int nj = 0, nk = 0;
                    if (dx == 1) nj = 0;
                    else if (dx == -1) nj = 2;
                    else if (dy == 1) nj = 1;
                    else if (dy == -1) nj = 3;

                    if (nx == p[i + 1].x && ny == p[i + 1].y) {
                        nk = k;
                    } else {
                        nk = 0;
                    }

                    f[i + 1][nj][nk] = max(f[i + 1][nj][nk], f[i][j][k] + 1);
                }
            }
        }
    }

    int ans = 0;
    for (int i = 0; i < 4; i++) {
        for (int j = 0; j < 4; j++) {
            ans = max(ans, f[n][i][j]);
        }
    }

    cout << log2(ans) << endl;

    return 0;
}
