import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static class Point {
        int x, y, d;

        public Point(int x, int y, int d) {
            this.x = x;
            this.y = y;
            this.d = d;
        }
    }

    static class Pair {
        int x, y;

        public Pair(int x, int y) {
            this.x = x;
            this.y = y;
        }
    }

    static int log2(int n) {
        return (int) (Math.log(n) / Math.log(2));
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        int n = Integer.parseInt(st.nextToken());
        Point[] points = new Point[n];
        for (int i = 0; i < n; i++) {
            st = new StringTokenizer(br.readLine());
            int x = Integer.parseInt(st.nextToken());
            int y = Integer.parseInt(st.nextToken());
            int d = 0;
            if (st.nextToken().equals(">")) d = 0;
            else if (st.nextToken().equals("v")) d = 1;
            else if (st.nextToken().equals("<")) d = 2;
            else d = 3;
            points[i] = new Point(x, y, d);
        }

        int[][] dp = new int[n][4];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 4; j++) {
                dp[i][j] = 1;
            }
        }

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 4; j++) {
                if (points[i].d == j) {
                    for (int k = 0; k < 4; k++) {
                        dp[i][k] = Math.max(dp[i][k], dp[i][j] * dp[find(points, points[i].x, points[i].y, k)][k]);
                    }
                }
            }
        }

        int max = 0;
        for (int i = 0; i < 4; i++) {
            max = Math.max(max, dp[n - 1][i]);
        }

        pw.println(log2(max));
        pw.close();
    }

    static int find(Point[] points, int x, int y, int d) {
        for (int i = 0; i < points.length; i++) {
            if (points[i].x == x && points[i].y == y && points[i].d == d) {
                return i;
            }
        }
        return -1;
    }
}
