import math
import sys

def solve():
    n = int(input())
    field = []
    for _ in range(n):
        x, y, d = input().split()
        field.append((int(x), int(y), d))

    dp = [[0] * (n + 1) for _ in range(n + 1)]
    for i in range(n):
        for j in range(n):
            if i == j:
                dp[i][j] = 1
            else:
                if field[i][2] == field[j][2]:
                    dp[i][j] = 0
                else:
                    if field[i][2] == '>':
                        if field[j][2] == '^':
                            dp[i][j] = 1
                        elif field[j][2] == '<':
                            dp[i][j] = 1
                        else:
                            dp[i][j] = 0
                    elif field[i][2] == 'v':
                        if field[j][2] == '^':
                            dp[i][j] = 1
                        elif field[j][2] == '<':
                            dp[i][j] = 1
                        else:
                            dp[i][j] = 0
                    elif field[i][2] == '<':
                        if field[j][2] == '^':
                            dp[i][j] = 1
                        elif field[j][2] == '>':
                            dp[i][j] = 1
                        else:
                            dp[i][j] = 0
                    else:
                        if field[j][2] == '^':
                            dp[i][j] = 1
                        elif field[j][2] == '>':
                            dp[i][j] = 1
                        else:
                            dp[i][j] = 0

    for k in range(1, n + 1):
        for i in range(n + 1):
            for j in range(n + 1):
                dp[i][j] = max(dp[i][j], dp[i][k] + dp[k][j])

    ans = 0
    for i in range(n):
        for j in range(n):
            if i != j:
                ans = max(ans, dp[i][j])

    print(math.floor(math.log2(ans)))

if __name__ == "__main__":
    solve()
