import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        scanner.close();

        if (n == 2) {
            System.out.println(1);
            return;
        }

        for (int k = 1; ; k++) {
            boolean found = true;
            for (int a = 1; a < n; a++) {
                long x = a;
                for (int i = 0; i < k; i++) {
                    x = (x * a) % n;
                }
                if (x != a) {
                    found = false;
                    break;
                }
            }
            if (found) {
                System.out.println(k);
                return;
            }
        }
    }
}
