#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define N 100000
#define M 100000
#define Q 100000

typedef struct {
    int x, y;
} Point;

typedef struct {
    int u, v;
} Edge;

typedef struct {
    int p, q;
} Query;

int n, m, q;
Point p[N + 1];
Edge e[M + 1];
Query q[Q + 1];

int find(int i) {
    if (p[i].x == p[i].y) return i;
    int j = find(p[i].x);
    p[i].x = p[j].x;
    p[i].y = p[j].y;
    return j;
}

int join(int i, int j) {
    i = find(i);
    j = find(j);
    if (i == j) return 0;
    if (p[i].x < p[j].x) {
        p[j].x = i;
        p[j].y = j;
    } else {
        p[i].x = j;
        p[i].y = i;
    }
    return 1;
}

int main() {
    scanf("%d %d %d", &n, &m, &q);
    for (int i = 1; i <= n; i++) {
        scanf("%d %d", &p[i].x, &p[i].y);
    }
    for (int i = 1; i <= m; i++) {
        scanf("%d %d", &e[i].u, &e[i].v);
        join(e[i].u, e[i].v);
    }
    for (int i = 1; i <= q; i++) {
        scanf("%d %d", &q[i].p, &q[i].q);
    }
    for (int i = 1; i <= q; i++) {
        int ok = 0;
        for (int j = 1; j <= m; j++) {
            if (find(q[i].p) == find(e[j].u) && find(q[i].q) == find(e[j].v)) {
                ok = 1;
                break;
            }
            if (find(q[i].p) == find(e[j].v) && find(q[i].q) == find(e[j].u)) {
                ok = 1;
                break;
            }
        }
        printf("%s\n", ok ? "Yes" : "No");
    }
    return 0;
}
