import sys
import heapq

def dijkstra(n, adj, p):
    dist = [[float('inf')] * (n + 1) for _ in range(n + 1)]
    for i in range(1, n + 1):
        dist[i][i] = 0
    for u in range(1, n + 1):
        for v in adj[u]:
            dist[u][v] = dist[v][u] = adj[u][v][0]
    for k in range(1, n + 1):
        for i in range(1, n + 1):
            for j in range(1, n + 1):
                dist[i][j] = min(dist[i][j], dist[i][k] + dist[k][j])
    dp = [[0.0] * (p + 1) for _ in range(n + 1)]
    for u in range(1, n + 1):
        for t in range(1, p + 1):
            for v in adj[u]:
                if t >= adj[u][v][0]:
                    dp[u][t] = max(dp[u][t], dp[u][t - adj[u][v][0]] + (t - adj[u][v][0]) * adj[u][v][1] / adj[u][v][0])
                if t >= dist[u][v]:
                    dp[v][t - dist[u][v]] = max(dp[v][t - dist[u][v]], dp[u][t] + (t - dist[u][v]) * adj[u][v][1] / adj[u][v][0])
    return dp

def solve():
    n, m, p = map(int, sys.stdin.readline().split())
    adj = [[] for _ in range(n + 1)]
    for _ in range(m):
        u, v, d, v_ = map(int, sys.stdin.readline().split())
        adj[u].append((v, d, v_))
        adj[v].append((u, d, v_))
    dp = dijkstra(n, adj, p)
    print(max(dp[1][p]))

if __name__ == '__main__':
    solve()
