#include <stdio.h>
#include <stdlib.h>

#define MAX_N 1000000

int main() {
    int A, B, C, N;
    int x[MAX_N], y[MAX_N], z[MAX_N];
    int vis[1000000][1000000][1000000];
    int cnt = 0;

    scanf("%d %d %d %d", &A, &B, &C, &N);

    for (int i = 0; i < N; i++) {
        scanf("%d %d %d", &x[i], &y[i], &z[i]);
    }

    for (int i = 0; i < N; i++) {
        vis[x[i]][y[i]][z[i]] = 1;
    }

    int surface_area = 6 * A * B * C;

    for (int i = 0; i < A; i++) {
        for (int j = 0; j < B; j++) {
            for (int k = 0; k < C; k++) {
                if (vis[i][j][k] == 0) {
                    int temp = 0;
                    if (i > 0 && vis[i - 1][j][k] == 0) temp++;
                    if (j > 0 && vis[i][j - 1][k] == 0) temp++;
                    if (k > 0 && vis[i][j][k - 1] == 0) temp++;
                    if (i < A - 1 && vis[i + 1][j][k] == 0) temp++;
                    if (j < B - 1 && vis[i][j + 1][k] == 0) temp++;
                    if (k < C - 1 && vis[i][j][k + 1] == 0) temp++;
                    surface_area -= temp;
                }
            }
        }
    }

    printf("%d\n", surface_area);

    return 0;
}
