#include <iostream>
#include <vector>
#include <set>
#include <algorithm>

using namespace std;

const int MAXN = 1e5 + 5;

int A, B, C, N;
vector<int> x, y, z;
set<pair<int, pair<int, int>>> vis;

int get(int a, int b, int c) {
    if (a == 0 || a == A - 1 || b == 0 || b == B - 1 || c == 0 || c == C - 1) return 1;
    if (vis.find({a, {b, c}}) != vis.end()) return 0;
    vis.insert({a, {b, c}});
    return 1 + get(a + 1, b, c) + get(a - 1, b, c) + get(a, b + 1, c) + get(a, b - 1, c) + get(a, b, c + 1) + get(a, b, c - 1);
}

int main() {
    cin >> A >> B >> C >> N;
    for (int i = 0; i < N; i++) {
        int a, b, c;
        cin >> a >> b >> c;
        x.push_back(a);
        y.push_back(b);
        z.push_back(c);
    }
    int ans = 2 * (A * B + A * C + B * C);
    for (int i = 0; i < N; i++) {
        vis.clear();
        ans -= get(x[i], y[i], z[i]);
    }
    cout << ans << endl;
    return 0;
}
