#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Song {
    int t, p, f;
};

bool compareSongs(const Song& a, const Song& b) {
    return a.t < b.t;
}

int dp[4005][4005];
Song songs[4005];

int main() {
    int N, T;
    cin >> N >> T;

    for (int i = 0; i < N; i++) {
        cin >> songs[i].t >> songs[i].p >> songs[i].f;
    }

    sort(songs, songs + N, compareSongs);

    for (int i = 0; i <= N; i++) {
        for (int j = 0; j <= T; j++) {
            dp[i][j] = -1;
        }
    }

    dp[0][0] = 0;

    for (int i = 0; i < N; i++) {
        for (int j = 0; j <= T; j++) {
            if (dp[i][j] == -1) continue;

            if (j + songs[i].t <= T) {
                dp[i + 1][j + songs[i].t] = max(dp[i + 1][j + songs[i].t], dp[i][j] + songs[i].p - (songs[i].f - songs[i].f) * (songs[i].f - songs[i].f));
                dp[i + 1][j + songs[i].t] = max(dp[i + 1][j + songs[i].t], dp[i][j]);
            }

            dp[i + 1][j] = max(dp[i + 1][j], dp[i][j]);
        }
    }

    int maxSatisfaction = 0;
    for (int i = 0; i <= T; i++) {
        maxSatisfaction = max(maxSatisfaction, dp[N][i]);
    }

    cout << maxSatisfaction << endl;

    return 0;
}
