import java.util.Arrays;
import java.util.Scanner;

public class Main {
    static class Song implements Comparable<Song> {
        int t, p, f;

        public Song(int t, int p, int f) {
            this.t = t;
            this.p = p;
            this.f = f;
        }

        @Override
        public int compareTo(Song o) {
            return Integer.compare(t, o.t);
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int t = scanner.nextInt();
        Song[] songs = new Song[n];
        for (int i = 0; i < n; i++) {
            songs[i] = new Song(scanner.nextInt(), scanner.nextInt(), scanner.nextInt());
        }
        Arrays.sort(songs);
        long[][] dp = new long[n + 1][t + 1];
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= t; j++) {
                if (songs[i - 1].t > j) {
                    dp[i][j] = dp[i - 1][j];
                } else {
                    dp[i][j] = Math.max(dp[i - 1][j], dp[i - 1][j - songs[i - 1].t] + songs[i - 1].p - (songs[i - 1].f - songs[i - 2].f) * (songs[i - 1].f - songs[i - 2].f));
                }
            }
        }
        System.out.println(dp[n][t]);
    }
}
