import sys

def solve():
    N, T = map(int, input().split())
    songs = []
    for _ in range(N):
        t, p, f = map(int, input().split())
        songs.append((t, p, f))

    dp = [[-1] * (T + 1) for _ in range(N + 1)]
    dp[0][0] = 0

    for i in range(1, N + 1):
        for j in range(T + 1):
            if dp[i - 1][j] == -1:
                continue
            for k in range(j + 1):
                if k + songs[i - 1][0] <= T:
                    new_val = dp[i - 1][j] + songs[i - 1][1] - (songs[i - 1][2] - songs[k - 1][2]) ** 2
                    if dp[i][k + songs[i - 1][0]] == -1 or new_val > dp[i][k + songs[i - 1][0]]:
                        dp[i][k + songs[i - 1][0]] = new_val

    ans = -1
    for i in range(T + 1):
        if dp[N][i] != -1 and (ans == -1 or dp[N][i] > ans):
            ans = dp[N][i]

    print(ans)

if __name__ == "__main__":
    solve()
