#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005

int n, m;
int c[MAXN];
int p[MAXN];
int g[MAXN][MAXN];
int in[MAXN];
int out[MAXN];
int cnt;
int ans;

void dfs(int u) {
    in[u] = 1;
    for (int v = 0; v < n; v++) {
        if (g[u][v] && !in[v]) {
            dfs(v);
        }
    }
    out[u] = 1;
    cnt++;
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d", &c[i]);
        p[i] = c[i];
    }
    scanf("%d", &m);
    for (int i = 0; i < m; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        u--; v--;
        g[u][v] = g[v][u] = 1;
    }
    for (int i = 0; i < n; i++) {
        if (!in[i]) {
            dfs(i);
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (g[i][j] && c[i] > c[j]) {
                p[j] = p[i] + 1;
            }
        }
    }
    for (int i = 0; i < n; i++) {
        ans += p[i];
    }
    printf("%d\n", ans);
    return 0;
}
