#include <iostream>
#include <vector>
#include <algorithm>
#include <queue>
#include <set>

using namespace std;

const int MAXN = 1e5 + 5;

int n, m;
vector<int> g[MAXN];
int c[MAXN];
int p[MAXN];
int d[MAXN];
int cnt[MAXN];
set<int> s[MAXN];

void dfs(int u) {
    for (int v : g[u]) {
        if (c[v] > c[u]) {
            p[v] = p[u] + 1;
            d[v] = d[u] + 1;
            cnt[v] = cnt[u] + 1;
            s[v].insert(p[v]);
            dfs(v);
        } else {
            p[v] = p[u] + 1;
            d[v] = d[u] + 1;
            cnt[v] = cnt[u] + 1;
            s[v].insert(p[v]);
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n;
    for (int i = 1; i <= n; i++) {
        cin >> c[i];
    }
    cin >> m;
    for (int i = 1; i <= m; i++) {
        int u, v;
        cin >> u >> v;
        g[u].push_back(v);
        g[v].push_back(u);
    }

    for (int i = 1; i <= n; i++) {
        if (d[i] == 0) {
            dfs(i);
        }
    }

    long long ans = 0;
    for (int i = 1; i <= n; i++) {
        if (d[i] == 1) {
            p[i] = 1;
        }
        ans += p[i];
    }

    cout << ans << '\n';

    return 0;
}
