#include <iostream>
#include <vector>
#include <algorithm>

int countCombinations(int n) {
    std::vector<int> combinations;
    for (int a = 0; a <= n; a++) {
        for (int b = 0; b <= n; b++) {
            int c = n - a - b;
            if (c >= 0 && c <= 9) {
                combinations.push_back(c);
            }
        }
    }
    std::sort(combinations.begin(), combinations.end());
    int count = 0;
    for (int i = 0; i < combinations.size(); i++) {
        if (combinations[i] == combinations[i + 1]) {
            count++;
        } else {
            count++;
            i++;
        }
    }
    return count;
}

int main() {
    int t;
    std::cin >> t;
    while (t--) {
        int n;
        std::cin >> n;
        std::cout << countCombinations(n) << std::endl;
    }
    return 0;
}
