#include <stdio.h>
#include <string.h>
#include <stdlib.h>

#define MAXN 10

int cmp(const void *a, const void *b) {
    int x = *(int *)a;
    int y = *(int *)b;
    return (x - y);
}

int main() {
    char s[MAXN + 1];
    scanf("%s", s);
    int n = strlen(s);
    int a[n];
    for (int i = 0; i < n; i++) {
        a[i] = s[i] - '0';
    }
    qsort(a, n, sizeof(int), cmp);
    int b[n];
    int j = 0;
    for (int i = 0; i < n; i++) {
        if (i == 0 || a[i] != a[i - 1]) {
            b[j++] = a[i];
        }
    }
    int c[n];
    for (int i = 0; i < n; i++) {
        c[i] = 10 - b[i];
    }
    qsort(c, n, sizeof(int), cmp);
    int d[n];
    j = 0;
    for (int i = 0; i < n; i++) {
        if (i == 0 || c[i] != c[i - 1]) {
            d[j++] = c[i];
        }
    }
    int max = 0;
    int max_index = 0;
    for (int i = 0; i < n; i++) {
        int diff = 0;
        for (int j = 0; j < n; j++) {
            diff += abs(d[i] - b[j]);
        }
        if (diff > max) {
            max = diff;
            max_index = i;
        }
    }
    for (int i = 0; i < n; i++) {
        printf("%d", d[max_index]);
    }
    printf("\n");
    return 0;
}
