#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <set>
#include <cmath>

using namespace std;

int main() {
    string s;
    cin >> s;
    int n = s.size();
    vector<int> a(n);
    for (int i = 0; i < n; i++) {
        a[i] = s[i] - '0';
    }
    sort(a.begin(), a.end());
    vector<int> b(n);
    int j = 0;
    for (int i = 0; i < n; i++) {
        if (i == 0 || a[i] != a[i - 1]) {
            b[j++] = a[i];
        }
    }
    int m = j;
    vector<int> c(n);
    for (int i = 0; i < n; i++) {
        c[i] = 10;
    }
    for (int i = 0; i < m; i++) {
        c[b[i]] = i;
    }
    int max_diff = 0;
    int min_num = 0;
    do {
        int diff = 0;
        for (int i = 0; i < n; i++) {
            diff += min(abs(a[i] - b[i]), 10 - abs(a[i] - b[i]));
        }
        if (diff > max_diff) {
            max_diff = diff;
            min_num = 0;
            for (int i = 0; i < n; i++) {
                min_num = min_num * 10 + b[i];
            }
        } else if (diff == max_diff) {
            int num = 0;
            for (int i = 0; i < n; i++) {
                num = num * 10 + b[i];
            }
            if (num < min_num) {
                min_num = num;
            }
        }
        int k = 0;
        for (int i = 0; i < m; i++) {
            if (c[b[i]] == n - 1) {
                b[k++] = b[i];
                c[b[i]] = n - 1;
            } else {
                c[b[i]]++;
            }
        }
        for (int i = 0; i < m; i++) {
            if (c[b[i]] == n) {
                b[k++] = b[i];
                c[b[i]] = n;
            }
        }
        m = k;
    } while (next_permutation(b.begin(), b.begin() + m));
    for (int i = 0; i < n; i++) {
        cout << b[i];
    }
    cout << endl;
    return 0;
}
