import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] x = new int[n];
        int[] y = new int[n];
        for (int i = 0; i < n; i++) {
            char c = scanner.next().charAt(0);
            x[i] = c == 'y' ? 1 : 0;
            y[i] = scanner.nextInt();
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (x[i] == 1) {
                int j = i;
                while (j < n && x[j] == 1) {
                    j++;
                }
                if (j - i > 1) {
                    ans++;
                }
                i = j - 1;
            }
        }
        System.out.println(ans);
    }
}
