#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 105
#define MAX_L 1005

int n, l;
char s[MAX_L];
int a[MAX_N][MAX_N];

void shift_row(int i, int dir) {
    int temp = a[i][n - 1];
    for (int j = n - 1; j > 0; j--) {
        a[i][j] = a[i][j - 1];
    }
    a[i][0] = temp;
}

void shift_col(int j, int dir) {
    int temp = a[n - 1][j];
    for (int i = n - 1; i > 0; i--) {
        a[i][j] = a[i - 1][j];
    }
    a[0][j] = temp;
}

void apply_operation(char *op) {
    int i = 0, j = 0, dir = 0;
    if (op[0] == 'L') {
        dir = -1;
        i = op[1] - '0';
    } else if (op[0] == 'R') {
        dir = 1;
        i = op[1] - '0';
    } else if (op[0] == 'U') {
        dir = -1;
        j = op[1] - '0';
    } else if (op[0] == 'D') {
        dir = 1;
        j = op[1] - '0';
    }
    if (dir == -1) {
        shift_row(i, dir);
    } else {
        shift_col(j, dir);
    }
}

void apply_repetition(char *op) {
    int m = 0;
    sscanf(op + 2, "%d", &m);
    op[2] = '\0';
    int len = strlen(op);
    for (int i = 0; i < m; i++) {
        for (int j = 0; j < len; j++) {
            apply_operation(op + j);
        }
    }
}

void apply_sequence() {
    int len = strlen(s);
    for (int i = 0; i < len; i++) {
        if (s[i] == '(') {
            int j = i;
            while (s[j] != ')') {
                j++;
            }
            apply_repetition(s + i);
            i = j;
        } else {
            apply_operation(s + i);
        }
    }
}

void print_matrix() {
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            printf("%d ", a[i][j]);
        }
        printf("\n");
    }
}

int main() {
    scanf("%d %d %s", &n, &l, s);
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            a[i][j] = (i * n) + j + 1;
        }
    }
    apply_sequence();
    print_matrix();
    return 0;
}
