#include <iostream>
#include <vector>
#include <string>
#include <stack>
#include <sstream>

using namespace std;

int main() {
    int n, l;
    cin >> n >> l;

    string s;
    cin >> s;

    vector<vector<int>> a(n, vector<int>(n));
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            a[i][j] = (i * n) + j + 1;
        }
    }

    stack<pair<int, int>> st;
    for (int i = 0; i < l; i++) {
        if (s[i] == '(') {
            int j = i + 1;
            while (s[j] != ')') {
                j++;
            }
            int m = stoi(s.substr(j + 1, s.find(')', j) - j - 1));
            for (int k = 0; k < m; k++) {
                int j = i + 1;
                while (s[j] != ' ') {
                    j++;
                }
                int num = stoi(s.substr(j + 1, s.find(' ', j) - j - 1));
                if (s[i] == 'L') {
                    st.push(make_pair(num, 1));
                } else if (s[i] == 'R') {
                    st.push(make_pair(num, -1));
                } else if (s[i] == 'U') {
                    st.push(make_pair(num, 0));
                } else if (s[i] == 'D') {
                    st.push(make_pair(num, 2));
                }
                i = j;
            }
            i = j;
        } else {
            int j = i + 1;
            while (s[j] != ' ') {
                j++;
            }
            int num = stoi(s.substr(i + 1, j - i - 1));
            if (s[i] == 'L') {
                st.push(make_pair(num, 1));
            } else if (s[i] == 'R') {
                st.push(make_pair(num, -1));
            } else if (s[i] == 'U') {
                st.push(make_pair(num, 0));
            } else if (s[i] == 'D') {
                st.push(make_pair(num, 2));
            }
            i = j;
        }
    }

    while (!st.empty()) {
        auto p = st.top();
        st.pop();
        int i = p.first - 1;
        int d = p.second;
        if (d == 1) {
            for (int j = n - 1; j >= 1; j--) {
                a[i][j] = a[i][j - 1];
            }
            a[i][0] = a[i][n - 1];
        } else if (d == -1) {
            for (int j = 0; j < n - 1; j++) {
                a[i][j] = a[i][j + 1];
            }
            a[i][n - 1] = a[i][0];
        } else if (d == 0) {
            for (int j = n - 1; j >= 1; j--) {
                a[j][i] = a[j - 1][i];
            }
            a[0][i] = a[n - 1][i];
        } else {
            for (int j = 0; j < n - 1; j++) {
                a[j][i] = a[j + 1][i];
            }
            a[n - 1][i] = a[0][i];
        }
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            cout << a[i][j] << " ";
        }
        cout << endl;
    }

    return 0;
}
