import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int l = scanner.nextInt();
        String s = scanner.next();
        int[][] a = new int[n][n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                a[i][j] = (i * n) + j + 1;
            }
        }
        int i = 0;
        while (i < l) {
            if (s.charAt(i) == '(') {
                int j = i + 1;
                int count = 0;
                while (s.charAt(j) != ')') {
                    if (Character.isDigit(s.charAt(j))) {
                        count = count * 10 + (s.charAt(j) - '0');
                        j++;
                    } else {
                        i = j - 1;
                        break;
                    }
                }
                i = j;
                int k = i + 1;
                while (Character.isDigit(s.charAt(k))) {
                    k++;
                }
                i = k;
                repeat(a, n, s.substring(i - count, i));
                i -= count;
            } else if (s.charAt(i) == 'L' || s.charAt(i) == 'R' || s.charAt(i) == 'U' || s.charAt(i) == 'D') {
                int j = i + 1;
                while (Character.isDigit(s.charAt(j))) {
                    j++;
                }
                int k = Integer.parseInt(s.substring(i + 1, j));
                i = j - 1;
                if (s.charAt(i) == 'L') {
                    leftShift(a, n, k);
                } else if (s.charAt(i) == 'R') {
                    rightShift(a, n, k);
                } else if (s.charAt(i) == 'U') {
                    upShift(a, n, k);
                } else if (s.charAt(i) == 'D') {
                    downShift(a, n, k);
                }
            }
            i++;
        }
        for (int[] row : a) {
            for (int num : row) {
                System.out.print(num + " ");
            }
            System.out.println();
        }
    }

    public static void repeat(int[][] a, int n, String s) {
        int i = 0;
        while (i < s.length()) {
            if (s.charAt(i) == 'L' || s.charAt(i) == 'R' || s.charAt(i) == 'U' || s.charAt(i) == 'D') {
                int j = i + 1;
                while (Character.isDigit(s.charAt(j))) {
                    j++;
                }
                int k = Integer.parseInt(s.substring(i + 1, j));
                i = j - 1;
                if (s.charAt(i) == 'L') {
                    leftShift(a, n, k);
                } else if (s.charAt(i) == 'R') {
                    rightShift(a, n, k);
                } else if (s.charAt(i) == 'U') {
                    upShift(a, n, k);
                } else if (s.charAt(i) == 'D') {
                    downShift(a, n, k);
                }
            }
            i++;
        }
    }

    public static void leftShift(int[][] a, int n, int k) {
        for (int i = 0; i < n; i++) {
            int temp = a[i][n - 1];
            for (int j = n - 1; j > 0; j--) {
                a[i][j] = a[i][j - 1];
            }
            a[i][0] = temp;
        }
    }

    public static void rightShift(int[][] a, int n, int k) {
        for (int i = 0; i < n; i++) {
            int temp = a[i][0];
            for (int j = 0; j < n - 1; j++) {
                a[i][j] = a[i][j + 1];
            }
            a[i][n - 1] = temp;
        }
    }

    public static void upShift(int[][] a, int n, int k) {
        for (int j = 0; j < n; j++) {
            int temp = a[n - 1][j];
            for (int i = n - 1; i > 0; i--) {
                a[i][j] = a[i - 1][j];
            }
            a[0][j] = temp;
        }
    }

    public static void downShift(int[][] a, int n, int k) {
        for (int j = 0; j < n; j++) {
            int temp = a[0][j];
            for (int i = 0; i < n - 1; i++) {
                a[i][j] = a[i + 1][j];
            }
            a[n - 1][j] = temp;
        }
    }
}
