import re

def shift_row(matrix, i, direction):
    if direction == 'L':
        matrix[i] = matrix[i][1:] + [matrix[i][0]]
    elif direction == 'R':
        matrix[i] = [matrix[i][-1]] + matrix[i][:-1]

def shift_col(matrix, j, direction):
    for row in matrix:
        if direction == 'U':
            row = row[-1:] + row[:-1]
        elif direction == 'D':
            row = row[1:] + [row[0]]

def apply_operations(matrix, operations):
    for op in operations:
        match = re.match(r'([LRUD])(\d+)', op)
        if match:
            direction, num = match.groups()
            num = int(num)
            if direction == 'L':
                shift_row(matrix, num - 1, direction)
            elif direction == 'R':
                shift_row(matrix, num - 1, direction)
            elif direction == 'U':
                shift_col(matrix, num - 1, direction)
            elif direction == 'D':
                shift_col(matrix, num - 1, direction)
        else:
            match = re.match(r'\(([^()]+)\)(\d+)', op)
            if match:
                subseq, num = match.groups()
                num = int(num)
                subseq = subseq.replace('L', 'L').replace('R', 'R').replace('U', 'U').replace('D', 'D')
                subseq = subseq.split()
                for _ in range(num):
                    apply_operations(matrix, subseq)

def print_matrix(matrix):
    for row in matrix:
        print(' '.join(map(str, row)))

def main():
    N, L = map(int, input().split())
    S = input()
    matrix = [[(i - 1) * N + j + 1 for j in range(N)] for i in range(N)]
    apply_operations(matrix, S)
    print_matrix(matrix)

if __name__ == '__main__':
    main()
