#include <iostream>
#include <vector>
#include <queue>
#include <set>
#include <algorithm>

using namespace std;

const int MAX_N = 10000;

int main() {
    int N, A, B, C;
    cin >> N >> A >> B >> C;

    vector<int> a(A), b(B), c(C);
    for (int i = 0; i < A; i++) {
        cin >> a[i];
    }
    for (int i = 0; i < B; i++) {
        cin >> b[i];
    }
    for (int i = 0; i < C; i++) {
        cin >> c[i];
    }

    int M;
    cin >> M;

    vector<vector<int>> graph(N + 1);
    for (int i = 0; i < M; i++) {
        int x, y;
        cin >> x >> y;
        graph[x].push_back(y);
        graph[y].push_back(x);
    }

    vector<int> color(N + 1, -1);
    vector<int> group(N + 1, 0);
    vector<int> group_size(N + 1, 0);

    queue<int> q;
    for (int i = 0; i < A; i++) {
        color[a[i]] = 1;
        group[a[i]] = 1;
        group_size[1]++;
        q.push(a[i]);
    }

    while (!q.empty()) {
        int u = q.front();
        q.pop();

        for (int v : graph[u]) {
            if (color[v] == -1) {
                color[v] = 1;
                group[v] = group[u];
                group_size[group[v]]++;
                q.push(v);
            }
        }
    }

    for (int i = 0; i < B; i++) {
        color[b[i]] = 2;
        group[b[i]] = 2;
        group_size[2]++;
        q.push(b[i]);
    }

    while (!q.empty()) {
        int u = q.front();
        q.pop();

        for (int v : graph[u]) {
            if (color[v] == -1) {
                color[v] = 2;
                group[v] = group[u];
                group_size[group[v]]++;
                q.push(v);
            }
        }
    }

    for (int i = 0; i < C; i++) {
        color[c[i]] = 3;
        group[c[i]] = 3;
        group_size[3]++;
        q.push(c[i]);
    }

    while (!q.empty()) {
        int u = q.front();
        q.pop();

        for (int v : graph[u]) {
            if (color[v] == -1) {
                color[v] = 3;
                group[v] = group[u];
                group_size[group[v]]++;
                q.push(v);
            }
        }
    }

    int min_comm = 3;
    int min_comm_id = 0;

    for (int i = 1; i <= N; i++) {
        if (color[i] == -1) {
            int comm = 0;
            for (int j = 1; j <= 3; j++) {
                if (group_size[j] > 0) {
                    comm++;
                }
            }
            if (comm < min_comm) {
                min_comm = comm;
                min_comm_id = i;
            }
        }
    }

    cout << min_comm << " " << min_comm_id << endl;

    return 0;
}
