from collections import deque

def bfs(start, visited, graph, grades):
    queue = deque([start])
    visited[start] = True
    count = 1
    while queue:
        node = queue.popleft()
        for neighbor in graph[node]:
            if not visited[neighbor]:
                queue.append(neighbor)
                visited[neighbor] = True
                count += 1
                if neighbor in grades:
                    return count
    return count

def main():
    N, A, B, C = map(int, input().split())
    grades = {i: set() for i in range(1, 4)}
    for i in range(1, A+1):
        grades[1].add(int(input()))
    for i in range(1, B+1):
        grades[2].add(int(input()))
    for i in range(1, C+1):
        grades[3].add(int(input()))
    M = int(input())
    graph = {i: set() for i in range(1, N+1)}
    for _ in range(M):
        x, y = map(int, input().split())
        graph[x].add(y)
        graph[y].add(x)
    visited = {i: False for i in range(1, N+1)}
    min_count = float('inf')
    min_node = None
    for i in range(1, N+1):
        if not visited[i]:
            count = bfs(i, visited, graph, grades)
            if count < min_count:
                min_count = count
                min_node = i
    print(min_count, min_node)

if __name__ == "__main__":
    main()
