#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007
#define N 110

typedef long long ll;

ll power(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n, m, d;
    scanf("%d %d %lld", &n, &m, &d);

    ll s[N], t[N], f[N];
    for (int i = 0; i < n; i++) scanf("%lld", &s[i]);
    for (int i = 0; i < n; i++) scanf("%lld", &t[i]);
    for (int i = 0; i < m; i++) scanf("%lld", &f[i]);

    ll ans = 0;
    for (ll i = 0; i < d; i++) {
        int id = i % n;
        ll sum = 0;
        for (int j = 0; j < n; j++) {
            int k = (j + i % n) % n;
            sum = (sum + s[k] * t[j]) % MOD;
        }
        s[id] = (s[id] + f[i % m]) % MOD;
        ans = (ans + sum) % MOD;
    }

    printf("%lld\n", ans);

    return 0;
}
