#include <stdio.h>
#include <stdlib.h>

#define MAXN 20005
#define MAXM 1000005

int a, b, c, n;
int x[MAXN], y[MAXN], z[MAXN];
int vis[MAXM];
int dx[] = {-1, 1, 0, 0, 0};
int dy[] = {0, 0, -1, 1, 0};
int dz[] = {0, 0, 0, 0, -1};

int dfs(int x, int y, int z) {
    if (x < 0 || x >= a || y < 0 || y >= b || z < 0 || z >= c || vis[x * b * c + y * c + z]) return 0;
    vis[x * b * c + y * c + z] = 1;
    for (int i = 0; i < 5; i++) {
        dfs(x + dx[i], y + dy[i], z + dz[i]);
    }
    return 1;
}

int main() {
    scanf("%d%d%d%d", &a, &b, &c, &n);
    for (int i = 1; i <= n; i++) {
        scanf("%d%d%d", &x[i], &y[i], &z[i]);
    }
    int cnt = 0;
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < 5; j++) {
            vis[x[i] * b * c + y[i] * c + z[i]] = 0;
        }
    }
    for (int i = 0; i < a; i++) {
        for (int j = 0; j < b; j++) {
            for (int k = 0; k < c; k++) {
                if (vis[i * b * c + j * c + k]) {
                    cnt++;
                    dfs(i, j, k);
                }
            }
        }
    }
    printf("%d\n", cnt);
    return 0;
}
