#include <iostream>
#include <vector>
#include <queue>
#include <set>

using namespace std;

const int MAXN = 1e6 + 10;

int A, B, C, N;
vector<int> X, Y, Z;
vector<vector<int>> adj;
vector<bool> vis;
int cnt;

void dfs(int u) {
    vis[u] = true;
    for (int v : adj[u]) {
        if (!vis[v]) {
            dfs(v);
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> A >> B >> C >> N;
    X.resize(N);
    Y.resize(N);
    Z.resize(N);
    for (int i = 0; i < N; i++) {
        cin >> X[i] >> Y[i] >> Z[i];
    }

    adj.resize(A * B * C);
    vis.resize(A * B * C);

    for (int i = 0; i < N; i++) {
        int x = X[i], y = Y[i], z = Z[i];
        for (int dx = -1; dx <= 1; dx++) {
            for (int dy = -1; dy <= 1; dy++) {
                for (int dz = -1; dz <= 1; dz++) {
                    if (dx == 0 && dy == 0 && dz == 0) continue;
                    int nx = x + dx, ny = y + dy, nz = z + dz;
                    if (nx >= 0 && nx < A && ny >= 0 && ny < B && nz >= 0 && nz < C) {
                        adj[x * B * C + y * C + z].push_back(nx * B * C + ny * C + nz);
                        adj[nx * B * C + ny * C + nz].push_back(x * B * C + y * C + z);
                    }
                }
            }
        }
    }

    for (int i = 0; i < A * B * C; i++) {
        if (!vis[i]) {
            cnt++;
            dfs(i);
        }
    }

    cout << cnt << '\n';

    return 0;
}
