import java.io.*;
import java.util.*;

public class Main {
    static int[][][] vis;
    static int[][][] dis;
    static int[][][] a;
    static int[] dx = {1, -1, 0, 0, 0};
    static int[] dy = {0, 0, 1, -1, 0};
    static int[] dz = {0, 0, 0, 0, 1};

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        int A = Integer.parseInt(st.nextToken());
        int B = Integer.parseInt(st.nextToken());
        int C = Integer.parseInt(st.nextToken());
        int N = Integer.parseInt(st.nextToken());
        a = new int[A][B][C];
        vis = new int[A][B][C];
        dis = new int[A][B][C];
        for (int i = 0; i < A; i++) {
            for (int j = 0; j < B; j++) {
                for (int k = 0; k < C; k++) {
                    dis[i][j][k] = -1;
                }
            }
        }
        for (int i = 0; i < N; i++) {
            st = new StringTokenizer(br.readLine());
            int x = Integer.parseInt(st.nextToken());
            int y = Integer.parseInt(st.nextToken());
            int z = Integer.parseInt(st.nextToken());
            a[x][y][z] = 1;
        }
        int cnt = 0;
        for (int i = 0; i < A; i++) {
            for (int j = 0; j < B; j++) {
                for (int k = 0; k < C; k++) {
                    if (a[i][j][k] == 0 && vis[i][j][k] == 0) {
                        dfs(i, j, k);
                        cnt++;
                    }
                }
            }
        }
        System.out.println(cnt);
    }

    static void dfs(int x, int y, int z) {
        vis[x][y][z] = 1;
        for (int i = 0; i < 5; i++) {
            int nx = x + dx[i];
            int ny = y + dy[i];
            int nz = z + dz[i];
            if (nx >= 0 && nx < vis.length && ny >= 0 && ny < vis[0].length && nz >= 0 && nz < vis[0][0].length && a[nx][ny][nz] == 0 && vis[nx][ny][nz] == 0) {
                dis[nx][ny][nz] = dis[x][y][z] + 1;
                dfs(nx, ny, nz);
            }
        }
    }
}
