#include <stdio.h>
#include <stdlib.h>

#define N 200000
#define K 200000

int max(int a, int b) {
    return (a > b) ? a : b;
}

int min(int a, int b) {
    return (a < b) ? a : b;
}

int main() {
    int n, k, t, l, r, x, i, j, a[N + 1], b[N + 1], c[N + 1], d[N + 1], e[N + 1], f[N + 1];

    scanf("%d %d %d", &n, &k, &t);
    for (i = 1; i <= t; i++) {
        scanf("%d %d %d", &l, &r, &x);
        a[x] = max(a[x], l);
        b[x] = min(b[x], r);
    }
    for (i = 1; i <= k; i++) {
        a[i] = max(a[i], a[i - 1]);
        b[i] = min(b[i], b[i - 1]);
    }
    for (i = 1; i <= k; i++) {
        if (a[i] <= b[i]) {
            c[i] = a[i];
            d[i] = b[i];
        } else {
            c[i] = a[i - 1];
            d[i] = b[i - 1];
        }
    }
    for (i = 1; i <= k; i++) {
        e[i] = max(e[i - 1], c[i]);
        f[i] = min(f[i - 1], d[i]);
    }
    j = 0;
    for (i = 1; i <= k; i++) {
        if (e[i] <= f[i]) {
            j++;
        }
    }
    printf("%d\n", n - j);
    return 0;
}
