#include <iostream>
#include <vector>
#include <algorithm>

struct Task {
    int l, r, x;
};

bool compareTasks(const Task& a, const Task& b) {
    return a.x < b.x;
}

int main() {
    int N, K, T;
    std::cin >> N >> K >> T;

    std::vector<Task> tasks(T);
    for (int i = 0; i < T; i++) {
        std::cin >> tasks[i].l >> tasks[i].r >> tasks[i].x;
    }

    std::sort(tasks.begin(), tasks.end(), compareTasks);

    std::vector<int> lastTask(N + 1, -1);
    int count = 0;

    for (int i = 0; i < T; i++) {
        int l = tasks[i].l, r = tasks[i].r, x = tasks[i].x;
        if (lastTask[x] == -1) {
            count += r - l + 1;
            for (int j = l; j <= r; j++) {
                lastTask[j] = x;
            }
        } else {
            for (int j = l; j <= std::min(r, lastTask[x] - 1); j++) {
                count++;
                lastTask[j] = x;
            }
            lastTask[x] = std::max(lastTask[x], r + 1);
        }
    }

    std::cout << count << std::endl;

    return 0;
}
