#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

const int MOD = 1000000007;

int main() {
    int N, M, D;
    std::cin >> N >> M >> D;

    std::vector<int> s(N), t(N), f(M);
    for (int i = 0; i < N; ++i) {
        std::cin >> s[i];
    }
    for (int i = 0; i < N; ++i) {
        std::cin >> t[i];
    }
    for (int i = 0; i < M; ++i) {
        std::cin >> f[i];
    }

    std::vector<long long> salary(N);
    for (int i = 0; i < N; ++i) {
        salary[i] = (s[i] % MOD) * t[i] % MOD;
    }

    std::vector<long long> total(N);
    for (int d = 0; d < D; ++d) {
        int staff = d % N;
        int promotion = (d % M);
        for (int i = 0; i < N; ++i) {
            salary[i] = (salary[i] + salary[i]) % MOD;
        }
        salary[staff] = (salary[staff] + salary[staff]) % MOD;
        salary[staff] = (salary[staff] + f[promotion]) % MOD;
        total[staff] = (total[staff] + salary[staff]) % MOD;
    }

    long long result = 0;
    for (int i = 0; i < N; ++i) {
        result = (result + total[i]) % MOD;
    }

    std::cout << result << std::endl;

    return 0;
}
