#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 205
#define M 2005
#define INF 0x3f3f3f3f

typedef struct {
    int v, u, d, c;
} Edge;

int n, m, P, s, t;
Edge e[M];
int head[N], edge_cnt;
double dis[N];

void add_edge(int v, int u, int d, int c) {
    e[edge_cnt].v = v;
    e[edge_cnt].u = u;
    e[edge_cnt].d = d;
    e[edge_cnt].c = c;
    head[v] = edge_cnt++;
}

void dijkstra(int s) {
    for (int i = 1; i <= n; i++) {
        dis[i] = INF;
    }
    dis[s] = 0;
    for (int i = 1; i <= n; i++) {
        int u = -1;
        for (int j = 1; j <= n; j++) {
            if (dis[j] < INF && (u == -1 || dis[j] < dis[u])) {
                u = j;
            }
        }
        if (dis[u] == INF) {
            break;
        }
        for (int j = head[u]; j != -1; j = e[j].next) {
            int v = e[j].v;
            int d = e[j].d;
            int c = e[j].c;
            double new_dis = dis[u] + d;
            if (dis[v] > new_dis) {
                dis[v] = new_dis;
            }
        }
        dis[u] = INF;
    }
}

int main() {
    scanf("%d%d%d%d%d", &n, &m, &P, &s, &t);
    for (int i = 0; i < n; i++) {
        head[i] = -1;
    }
    for (int i = 0; i < m; i++) {
        scanf("%d%d%d%d", &e[i].v, &e[i].u, &e[i].d, &e[i].c);
        add_edge(e[i].v, e[i].u, e[i].d, e[i].c);
        add_edge(e[i].u, e[i].v, e[i].d, 0);
    }
    dijkstra(s);
    double ans = dis[t];
    for (int i = 0; i < m; i++) {
        int v = e[i].v;
        int u = e[i].u;
        int d = e[i].d;
        int c = e[i].c;
        double new_dis = dis[u] + d;
        double cost = (new_dis - dis[u]) * c;
        if (cost <= P) {
            P -= cost;
            dis[u] = new_dis;
            dijkstra(s);
            ans = dis[t];
        }
    }
    printf("%.6lf\n", ans);
    return 0;
}
