#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

const int N = 205;
const int M = 2005;
const long long INF = 1e18;

struct Edge {
    int to, next, cost, len;
};

Edge e[M << 1];
int head[N], tot;
long long dis[N], f[N], mx[N], val[N];
bool vis[N];

void addEdge(int u, int v, int c, int l) {
    e[++tot] = {v, head[u], c, l};
    head[u] = tot;
}

void dijkstra(int s) {
    for (int i = 1; i <= n; i++) {
        dis[i] = INF;
        vis[i] = false;
    }
    dis[s] = 0;
    priority_queue<pair<long long, int>, vector<pair<long long, int>>, greater<pair<long long, int>>> q;
    q.push({0, s});
    while (!q.empty()) {
        int u = q.top().second;
        q.pop();
        if (vis[u]) continue;
        vis[u] = true;
        for (int i = head[u]; i; i = e[i].next) {
            int v = e[i].to;
            if (dis[v] > dis[u] + e[i].len) {
                dis[v] = dis[u] + e[i].len;
                q.push({dis[v], v});
            }
        }
    }
}

int main() {
    int n, m, P, s, t;
    cin >> n >> m >> P >> s >> t;
    for (int i = 1; i <= m; i++) {
        int u, v, c, l;
        cin >> u >> v >> c >> l;
        addEdge(u, v, l, c);
    }
    for (int i = 1; i <= n; i++) {
        dijkstra(i);
        for (int j = 1; j <= n; j++) {
            if (dis[j] != INF) {
                f[i] = max(f[i], dis[j]);
                mx[j] = max(mx[j], dis[j]);
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        val[i] = f[i];
    }
    for (int i = 1; i <= n; i++) {
        for (int j = head[i]; j; j = e[j].next) {
            int v = e[j].to;
            if (mx[v] == dis[v]) {
                val[v] = max(val[v], val[i] + (e[j].len - dis[v]) * e[j].cost);
            }
        }
    }
    cout << fixed << setprecision(6) << val[t] << endl;
    return 0;
}
