import java.io.*;
import java.util.*;

public class Main {
    static final int N = 205;
    static final int M = 2005;
    static final int INF = 0x3f3f3f3f;
    static final double EPS = 1e-9;

    static int n, m, s, t, P;
    static int[] h = new int[N];
    static int[] e = new int[M];
    static int[] ne = new int[M];
    static int[] w = new int[M];
    static int[] c = new int[M];
    static int[] d = new int[N];
    static int[] f = new int[N];
    static int[] cur = new int[N];
    static double[] val = new double[N];
    static double[] mx = new double[N];

    static void add(int a, int b, int c, int w) {
        e[m] = b;
        ne[m] = h[a];
        w[m] = w;
        c[m] = c;
        h[a] = m++;
    }

    static boolean spfa() {
        Arrays.fill(d, INF);
        Arrays.fill(f, 0);
        Arrays.fill(cur, 0);
        Arrays.fill(val, 0);
        Arrays.fill(mx, 0);
        Queue<Integer> q = new LinkedList<>();
        q.add(s);
        d[s] = 0;
        while (!q.isEmpty()) {
            int t = q.poll();
            for (int i = h[t]; i != -1; i = ne[i]) {
                int j = e[i];
                if (d[j] > d[t] + w[i]) {
                    d[j] = d[t] + w[i];
                    f[j] = f[t] + c[i];
                    val[j] = val[t] + (double) c[i] * w[i];
                    mx[j] = Math.max(mx[t], val[t] + (double) c[i] * w[i]);
                    cur[j] = i;
                    q.add(j);
                }
            }
        }
        return d[t] != INF;
    }

    static void update(int x, double v) {
        if (d[x] == d[t] && mx[x] <= v) {
            val[t] = v;
            mx[t] = Math.max(mx[t], v);
        }
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);
        n = sc.nextInt();
        m = sc.nextInt();
        P = sc.nextInt();
        s = sc.nextInt();
        t = sc.nextInt();
        for (int i = 0; i < n; i++) {
            h[i] = -1;
        }
        for (int i = 0; i < m; i++) {
            int a = sc.nextInt();
            int b = sc.nextInt();
            int c = sc.nextInt();
            int w = sc.nextInt();
            add(a, b, c, w);
            add(b, a, 0, 0);
        }
        while (spfa()) {
            int x = t;
            while (x != s) {
                int i = cur[x];
                int y = e[i ^ 1];
                if (f[x] - f[y] <= P) {
                    update(y, mx[x] - val[x]);
                }
                x = y;
            }
        }
        pw.printf("%.6f\n", mx[t]);
        pw.close();
    }
}

