import heapq

N, M, P, s, t = map(int, input().split())
edges = [[] for _ in range(N + 1)]
for _ in range(M):
    u, v, d, c = map(int, input().split())
    edges[u].append((v, d, c))

def dijkstra():
    dist = [float('inf')] * (N + 1)
    dist[s] = 0
    pq = [(0, s)]
    while pq:
        d, u = heapq.heappop(pq)
        if d > dist[u]:
            continue
        for v, l, c in edges[u]:
            if dist[v] > dist[u] + l:
                dist[v] = dist[u] + l
                heapq.heappush(pq, (dist[v], v))
    return dist

dist = dijkstra()
dp = [0] * (N + 1)
for i in range(1, N + 1):
    dp[i] = dist[i]

for i in range(1, N + 1):
    for v, l, c in edges[i]:
        dp[i] = max(dp[i], dp[v] + l)

for i in range(1, N + 1):
    for v, l, c in edges[i]:
        if dp[i] == dp[v] + l:
            cost = (dp[i] - l - 1) * c
            if cost <= P:
                dp[i] = l + 1 + (P - cost) / c

print(max(dp[1:]))
