#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 1000
#define K 50
#define MAX 100000

int min(int a, int b) {
    return (a < b) ? a : b;
}

int max(int a, int b) {
    return (a > b) ? a : b;
}

int dp[N + 1][K + 1];

int solve(int n, int k) {
    if (n == 1) {
        return 0;
    }
    if (k == 0) {
        return (1 << 30);
    }
    if (dp[n][k] != -1) {
        return dp[n][k];
    }
    int ans = (1 << 30);
    for (int i = 0; i < n; i++) {
        int left = solve(i, k - 1);
        int right = solve(n - i - 1, k - 1);
        ans = min(ans, left + right + max(abs(a[i] - a[n - 1]), abs(a[i] - a[n - 2])));
    }
    return dp[n][k] = ans;
}

int main() {
    int n, k;
    int a[N];
    scanf("%d %d", &n, &k);
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= k; j++) {
            dp[i][j] = -1;
        }
    }
    printf("%d\n", solve(n, k));
    return 0;
}
