import java.util.Arrays;
import java.util.Scanner;

public class Main {
    static int N, K;
    static int[] A;

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        N = scanner.nextInt();
        K = scanner.nextInt();
        A = new int[N];
        for (int i = 0; i < N; i++) {
            A[i] = scanner.nextInt();
        }
        Arrays.sort(A);
        System.out.println(solve());
    }

    static long solve() {
        long[][] dp = new long[N + 1][N + 1];
        for (int i = 0; i <= N; i++) {
            Arrays.fill(dp[i], Long.MAX_VALUE / 2);
        }
        dp[0][0] = 0;
        for (int i = 1; i <= N; i++) {
            for (int j = 0; j <= i; j++) {
                if (j > 0) {
                    dp[i][j] = Math.min(dp[i][j], dp[i - 1][j - 1] + Math.abs(A[i - 1] - A[i - 2]));
                }
                if (j < i) {
                    dp[i][j] = Math.min(dp[i][j], dp[i - 1][j] + Math.abs(A[i - 1] - A[i - j - 1]));
                }
            }
        }
        return dp[N][N / 2];
    }
}
