#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 1000

int compare(const void *a, const void *b) {
    int x = *(int *)a;
    int y = *(int *)b;
    char str1[20], str2[20];
    sprintf(str1, "%d", x);
    sprintf(str2, "%d", y);
    int i;
    for (i = 0; str1[i] && str2[i]; i++) {
        if (str1[i] != str2[i]) {
            return str1[i] - str2[i];
        }
    }
    return strlen(str1) - strlen(str2);
}

int main() {
    int n;
    scanf("%d", &n);
    int a[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    qsort(a, n, sizeof(int), compare);
    int max_product = -1;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            char str[20];
            sprintf(str, "%d", a[i] * a[j]);
            int k;
            for (k = 0; k < strlen(str); k++) {
                if (k > 0 && str[k] != str[k - 1] + 1) {
                    break;
                }
            }
            if (k == strlen(str)) {
                max_product = (max_product == -1) ? a[i] * a[j] : (max_product > a[i] * a[j]) ? max_product : a[i] * a[j];
            }
        }
    }
    printf("%d\n", max_product);
    return 0;
}
