#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_H 200
#define MAX_W 200

typedef struct {
    int x, y;
} point;

int dx[] = {-1, 0, 1, 0};
int dy[] = {0, -1, 0, 1};

int h, w;
char map[MAX_H][MAX_W + 1];
int dist[MAX_H][MAX_W];
int soldier[MAX_H][MAX_W];
int visited[MAX_H][MAX_W];

int bfs(int sx, int sy, int tx, int ty) {
    int i, j, k, x, y;
    point q[1000];
    int front = 0, rear = 0;

    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            dist[i][j] = -1;
            visited[i][j] = 0;
        }
    }

    q[rear++] = (point) {sx, sy};
    dist[sx][sy] = 0;
    visited[sx][sy] = 1;

    while (front < rear) {
        x = q[front].x;
        y = q[front].y;
        front++;

        for (k = 0; k < 4; k++) {
            int nx = x + dx[k];
            int ny = y + dy[k];

            if (nx >= 0 && nx < h && ny >= 0 && ny < w && map[nx][ny] != '#' && !visited[nx][ny]) {
                if (nx == tx && ny == ty) {
                    return 0;
                }

                q[rear++] = (point) {nx, ny};
                dist[nx][ny] = dist[x][y] + 1;
                visited[nx][ny] = 1;
            }
        }
    }

    return 1;
}

int main() {
    int i, j, k, x, y, sx, sy, tx, ty;
    char c;

    scanf("%d %d", &h, &w);
    for (i = 0; i < h; i++) {
        scanf("%s", map[i]);
    }

    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (map[i][j] == '@') {
                sx = i;
                sy = j;
            } else if (map[i][j] == '%') {
                tx = i;
                ty = j;
            } else if (map[i][j] == '$') {
                soldier[i][j] = 1;
            }
        }
    }

    for (i = 0; i < h; i++) {
        for (j = 0; j < w; j++) {
            if (soldier[i][j] && bfs(sx, sy, i, j)) {
                printf("No\n");
                return 0;
            }
        }
    }

    printf("Yes\n");

    return 0;
}
