#include <iostream>
#include <vector>
#include <queue>
#include <string>
#include <algorithm>

using namespace std;

const int dx[] = {0, 0, 1, -1};
const int dy[] = {1, -1, 0, 0};

struct Node {
    int x, y, t;
};

int main() {
    int h, w;
    cin >> h >> w;

    vector<string> map(h);
    for (int i = 0; i < h; i++) {
        cin >> map[i];
    }

    int px = -1, py = -1;
    int ex = -1, ey = -1;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (map[i][j] == '@') {
                px = i;
                py = j;
            } else if (map[i][j] == '%') {
                ex = i;
                ey = j;
            }
        }
    }

    vector<vector<int>> dist(h, vector<int>(w, -1));
    queue<Node> q;
    q.push({px, py, 0});
    dist[px][py] = 0;

    while (!q.empty()) {
        Node node = q.front();
        q.pop();

        for (int i = 0; i < 4; i++) {
            int nx = node.x + dx[i];
            int ny = node.y + dy[i];

            if (nx < 0 || nx >= h || ny < 0 || ny >= w || map[nx][ny] == '#' || dist[nx][ny] != -1) {
                continue;
            }

            dist[nx][ny] = node.t + 1;
            q.push({nx, ny, node.t + 1});
        }
    }

    vector<vector<int>> soldierDist(h, vector<int>(w, -1));
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (map[i][j] == '$') {
                q.push({i, j, 0});
                soldierDist[i][j] = 0;

                while (!q.empty()) {
                    Node node = q.front();
                    q.pop();

                    for (int k = 0; k < 4; k++) {
                        int nx = node.x + dx[k];
                        int ny = node.y + dy[k];

                        if (nx < 0 || nx >= h || ny < 0 || ny >= w || map[nx][ny] == '#' || soldierDist[nx][ny] != -1) {
                            continue;
                        }

                        soldierDist[nx][ny] = node.t + 1;
                        q.push({nx, ny, node.t + 1});
                    }
                }
            }
        }
    }

    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (map[i][j] == '@') {
                for (int k = 0; k < 4; k++) {
                    int nx = i + dx[k];
                    int ny = j + dy[k];

                    if (nx < 0 || nx >= h || ny < 0 || ny >= w || map[nx][ny] == '#' || dist[nx][ny] == -1) {
                        continue;
                    }

                    int soldierTime = soldierDist[nx][ny];
                    int escapeTime = dist[ex][ey] - dist[nx][ny];

                    if (soldierTime <= escapeTime) {
                        cout << "No" << endl;
                        return 0;
                    }
                }
            }
        }
    }

    cout << "Yes" << endl;

    return 0;
}
