import java.util.*;
import java.io.*;

public class Main {
    static int[][] dir = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};
    static int H, W;
    static char[][] map;
    static int[] x, y;
    static int n;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int T = 1;
        while (T-- > 0) {
            String[] line = br.readLine().split(" ");
            H = Integer.parseInt(line[0]);
            W = Integer.parseInt(line[1]);
            map = new char[H][];
            for (int i = 0; i < H; i++) {
                map[i] = br.readLine().toCharArray();
            }
            x = new int[H * W];
            y = new int[H * W];
            n = 0;
            for (int i = 0; i < H; i++) {
                for (int j = 0; j < W; j++) {
                    if (map[i][j] == '@') {
                        x[n] = i;
                        y[n] = j;
                        n++;
                    }
                }
            }
            boolean[][] vis = new boolean[H][W];
            boolean flag = false;
            for (int i = 0; i < n; i++) {
                if (dfs(x[i], y[i], vis)) {
                    flag = true;
                    break;
                }
            }
            pw.println(flag ? "Yes" : "No");
        }
        pw.close();
    }

    static boolean dfs(int x, int y, boolean[][] vis) {
        vis[x][y] = true;
        for (int[] d : dir) {
            int nx = x + d[0];
            int ny = y + d[1];
            if (nx >= 0 && nx < H && ny >= 0 && ny < W && map[nx][ny] != '#' && !vis[nx][ny]) {
                if (map[nx][ny] == '%') return true;
                if (map[nx][ny] == '@') return false;
                if (map[nx][ny] == '$') {
                    for (int i = 0; i < n; i++) {
                        if (nx == x[i] && ny == y[i]) return false;
                    }
                }
                if (dfs(nx, ny, vis)) return true;
            }
        }
        return false;
    }
}
