from collections import deque

def can_escape(h, w, maps):
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    princess = None
    soldiers = []
    escape_hatch = None

    for i in range(h):
        for j in range(w):
            if maps[i][j] == '@':
                princess = (i, j)
            elif maps[i][j] == '%':
                escape_hatch = (i, j)
            elif maps[i][j] == '$':
                soldiers.append((i, j))

    if not princess or not escape_hatch:
        return "No"

    queue = deque([(princess, 0)])
    visited = set([princess])

    while queue:
        (x, y), step = queue.popleft()

        if (x, y) == escape_hatch:
            return "Yes"

        for dx, dy in directions:
            nx, ny = x + dx, y + dy

            if (0 <= nx < h) and (0 <= ny < w) and maps[nx][ny] != '#' and (nx, ny) not in visited:
                if (nx, ny) in soldiers:
                    continue
                queue.append(((nx, ny), step + 1))
                visited.add((nx, ny))

    return "No"

def main():
    h, w = map(int, input().split())
    maps = [list(input()) for _ in range(h)]

    print(can_escape(h, w, maps))

if __name__ == "__main__":
    main()
