import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static class Member implements Comparable<Member> {
        String name;
        int motivation;
        int rank;

        public Member(String name, int motivation) {
            this.name = name;
            this.motivation = motivation;
        }

        @Override
        public int compareTo(Member o) {
            if (motivation != o.motivation) {
                return o.motivation - motivation;
            } else {
                return name.compareTo(o.name);
            }
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int n = Integer.parseInt(br.readLine());
        Map<String, Member> map = new HashMap<>();
        List<Member> members = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            String[] s = br.readLine().split(" ");
            String name = s[0];
            int motivation = Integer.parseInt(s[1]);
            Member member = new Member(name, motivation);
            members.add(member);
            map.put(name, member);
        }
        Collections.sort(members);
        int m = Integer.parseInt(br.readLine());
        for (int i = 0; i < m; i++) {
            String[] s = br.readLine().split(" ");
            if (s[0].equals("+")) {
                String name = s[1];
                int motivation = Integer.parseInt(s[2]);
                Member member = new Member(name, motivation);
                members.add(member);
                map.put(name, member);
            } else {
                String name = s[1];
                map.remove(name);
                members.removeIf(member -> member.name.equals(name));
            }
            Collections.sort(members);
            int k = (int) (members.size() * 0.2);
            int count = 0;
            for (int j = 0; j < members.size(); j++) {
                if (j < k) {
                    members.get(j).rank = 1;
                } else {
                    members.get(j).rank = 0;
                }
                if (members.get(j).rank == 1) {
                    count++;
                }
            }
            if (count == 0) {
                pw.println();
            } else {
                for (int j = 0; j < members.size(); j++) {
                    if (members.get(j).rank == 1) {
                        if (map.get(members.get(j).name).rank == 0) {
                            pw.println(members.get(j).name + " is working hard now.");
                            map.get(members.get(j).name).rank = 1;
                        }
                    } else {
                        if (map.get(members.get(j).name).rank == 1) {
                            pw.println(members.get(j).name + " is not working now.");
                            map.get(members.get(j).name).rank = 0;
                        }
                    }
                }
            }
        }
        pw.close();
    }
}
